#include <intprg.h>
#include <sci8.h>
#include "common.h"
#include "gpio.h"
#include "cmt.h"




int main(void)
{
	uint8_t timer_status;
	uint8_t str[] = "Hello! from RX65N\r\n";

	gpio_create();
	cmt_create();

	led_ctrl(LED0, LED_ON);
	led_ctrl(LED1, LED_OFF);

	group_interrupt_create();
	sci8_create();
	sci8_start();
	sci8_send(str, 19);


	while(1) {
		led_toggle(LED0);
		led_toggle(LED1);
		cmtw0_start(MILLI_SEC, 500, &timer_status);
		while (!timer_status);
		led_toggle(LED0);
		led_toggle(LED1);
		cmtw1_start(MILLI_SEC, 500, &timer_status);
		while (!timer_status);
		led_toggle(LED0);
		led_toggle(LED1);
		cmtw0_start(MICRO_SEC, 50000, &timer_status);
		while (!timer_status);
		led_toggle(LED0);
		led_toggle(LED1);
		cmtw1_start(MICRO_SEC, 50000, &timer_status);
		while (!timer_status);
	}

	return 0;

}
