#include "common.h"
#include "gpio.h"
#include "cmt.h"
// #include "uart.h"
// #include "groupir.h"



int main(void)
{
	gpio_create();
	cmt_create();

	led_ctrl(LED0, LED_ON);
	led_ctrl(LED1, LED_OFF);

	cmt_wait(0, MILLI_SEC, 3000);

	while(1) {
		led_toggle(LED0);
		led_toggle(LED1);
		cmt_wait(0, MILLI_SEC, 500);
	}

	return 0;

}